package org.avis.management.web;import org.avis.router.Router;import org.avis.router.RouterOptionSet;import org.avis.router.RouterOptions;public class RunWebManager{  public static void main (String [] args) throws Exception  {    RouterOptionSet routerOptionSet = new RouterOptionSet ();        routerOptionSet.inheritFrom (WebManagementOptionSet.OPTION_SET);        RouterOptions config = new RouterOptions (routerOptionSet);        Router router = new Router (29170);        new WebManagementManager (router, config);  }}